import React, {Component} from 'react';
import {BrowserRouter as Router, Link, Route} from 'react-router-dom';
import {AnimatedSwitch} from 'react-router-transition';
// import {TransitionGroup, CSSTransition} from 'react-transition-group';
import Home from './Home';
import About from './About';
import Service from './Service';
import Contact from './Contact';
import Portfolio from './Portfolio';
import axios from 'axios';

class Header extends Component{
    constructor(props){
        super(props);
        this.state = {
            configData : {}
        }
    }
    componentDidMount(){
        axios.get('http://localhost/')
            .then(res => {
            // this.setState({configData : res})
            console.log(res);
        })
    }
    render(){
        return(
            <>
            <Router>
                <div className="topbar">
                    <div className="container">
                        <div className="row">
                            <div className="col-md-12"> 	  
                                <p className="pull-left hidden-xs"><i className="fa fa-clock-o"></i><span>Mon - Sat 8.00 - 18.00. Sunday CLOSED</span></p>
                                <p className="pull-right"><i className="fa fa-phone"></i>Tel No. (+001) 123-456-789</p>
                            </div>
                        </div>
                    </div>
                </div>
                <header>
                    <div className="navbar navbar-default navbar-static-top">
                        <div className="container">
                            <div className="navbar-header">
                                <button type="button" className="navbar-toggle" data-toggle="collapse" data-target=".navbar-collapse">
                                    <span className="icon-bar"></span>
                                    <span className="icon-bar"></span>
                                    <span className="icon-bar"></span>
                                </button>
                                <a className="navbar-brand" href="index.html"><img src="img/logo.png" alt="logo"/></a>
                            </div>
                            <div className="navbar-collapse collapse ">
                                <ul className="nav navbar-nav">
                                    <li><Link to={'/'}>Home</Link></li> 
                                    <li><Link to={'/about'}>About Us</Link></li> 
                                    <li><Link to={'/service'}>Service</Link></li>
                                    <li><Link to={'/portfolio'}>Portfolio</Link></li>
                                    <li><Link to={'/contact'}>Contact</Link></li>
                                </ul>
                            </div>
                        </div>
                    </div>
                </header>
                <AnimatedSwitch
                    atEnter={{ opacity: 0 }}
                    atLeave={{ opacity: 0 }}
                    atActive={{ opacity: 1 }}
                >
                    <Route exact path='/' data={this.state.configData.result}><Home /></Route>
                    <Route path='/about'><About /></Route>
                    <Route path='/service'><Service /></Route>
                    <Route path='/contact'><Contact /></Route>
                    <Route path='/portfolio'><Portfolio /></Route>
                </AnimatedSwitch>
            </Router>
            </>
        );
    }
}

export default Header;
