import React, {Component} from 'react';
import Carousel, {Modal, ModalGateway} from 'react-images';
import axios from 'axios';
// import Lightbox from 'react-lightbox-component';

class Gallery extends Component{
    constructor(props){
        super(props);
        this.state = {
            open : false,
            images : [],
        }
        this.openModal = this.openModal.bind(this);
        this.closeModal = this.closeModal.bind(this);
    }

    componentDidMount(){
        axios.get('http://103.56.149.24/category/getData',{
            method : 'GET',
            headers : {
                'Authorization' : 'eyJ0eXAiOiJKV1QiLCJhbGciOiJIUzI1NiJ9.eyJpc3MiOiJsdW1lbi1qd3QiLCJzdWIiOjEsImlhdCI6MTU2ODg2NzAwOX0.TsQnGt9TageOAfTlMDLZZr8QVQA-6w1_F-OGPlh__D8'
            }
        }).then(res => {
            console.log(res);
        });
    }

    openModal(e){
        e.preventDefault();
        this.setState({
            open : true,
            images : [{src : 'img/works/1.jpg'}]
        });
    }

    closeModal(){
        this.setState({
            open : false,
            images : null
        });
    }
    render(){
        const {open, images} = this.state;
        return(
            <>
                <section id="gallery-1" className="content-block section-wrapper gallery-1">
                    <div className="container">
                    <div className="editContent">
                        <ul className="filter">
                            <li className="active"><a href="# " data-filter="*">All</a></li>
                            <li><a href="# " data-filter=".artwork">Artwork</a></li>
                            <li><a href="# " data-filter=".creative">Creative</a></li>
                            <li><a href="# " data-filter=".nature">Nature</a></li>
                            <li><a href="# " data-filter=".outside">Outside</a></li>
                            <li><a href="# " data-filter=".photography">Photography</a></li>
                        </ul>
                    </div>
                    <div className="row">
                        <div id="isotope-gallery-container">
                            <div className="col-md-4 col-sm-6 col-xs-12 gallery-item-wrapper artwork creative">
                                <div className="gallery-item">
                                    <div className="gallery-thumb">
                                        <img src="img/works/1.jpg" className="img-responsive" alt="1st gallery Thumb"/>
                                        <div className="image-overlay"></div>
                                        <a href="# " className="gallery-zoom" onClick={this.openModal}><i className="fa fa-eye"></i></a>
                                        <a href="# " className="gallery-link"><i className="fa fa-link"></i></a>
                                    </div>
                                    <div className="gallery-details">
                                        <div className="editContent">
                                            <h5>1st gallery Item</h5>
                                        </div>
                                        <div className="editContent">
                                            <p>Nullam id dolor id nibh ultricies vehicula.</p>
                                        </div>
                                    </div>
                                </div>
                            </div>
                            <ModalGateway>
                                {open ?(
                                    <Modal onClose={this.closeModal}>
                                        <Carousel views={images} />
                                    </Modal>
                                )
                                    : null
                                }
                            </ModalGateway>
                        </div>
                    </div>
                </div>
            </section>
            </>
        );
    }
}

export default Gallery;
