import React, {Component} from 'react';
import {Link, Route, Switch} from 'react-router-dom';
// import {AnimatedSwitch} from 'react-router-transition';
// import {TransitionGroup, CSSTransition} from 'react-transition-group';
import Home from './Home';
import About from './About';
import Service from './Service';
import Contact from './Contact';
import Portfolio from './Portfolio';
import axios from 'axios';

class Header extends Component{
    constructor(props){
        super(props);
        this.state = {
            configData : {},
            contactData : {}
        }
    }
    componentDidMount(){
        axios.get('http://localhost/config')
            .then(res => {
            this.setState({configData : res.data.result})
        });

        axios.get('http://localhost/contact')
            .then(res => {
                this.setState({contactData : res.data.result[0]});
                console.table(res.data.result[0]);
        })
    }
    render(){
        const {configData} = this.state;
        return(
            <>
                <div className="topbar">
                    <div className="container">
                        <div className="row">
                            <div className="col-md-12"> 	  
                                <p className="pull-left hidden-xs"><i className="fa fa-clock-o"></i><span>{configData.configOfficeHour}</span></p>
                                <p className="pull-right"><i className="fa fa-phone"></i>Tel No. (+001) 123-456-789</p>
                            </div>
                        </div>
                    </div>
                </div>
                <header>
                    <div className="navbar navbar-default navbar-static-top">
                        <div className="container">
                            <div className="navbar-header">
                                <button type="button" className="navbar-toggle" data-toggle="collapse" data-target=".navbar-collapse">
                                    <span className="icon-bar"></span>
                                    <span className="icon-bar"></span>
                                    <span className="icon-bar"></span>
                                </button>
                                <a className="navbar-brand" href="index.html"><img src="img/logo.png" alt="logo"/></a>
                            </div>
                            <div className="navbar-collapse collapse ">
                                <ul className="nav navbar-nav">
                                    <li><Link to={'/'}>Home</Link></li> 
                                    <li><Link to={'/about'}>About Us</Link></li> 
                                    <li><Link to={'/service'}>Service</Link></li>
                                    <li><Link to={'/portfolio'}>Portfolio</Link></li>
                                    <li><Link to={'/contact'}>Contact</Link></li>
                                </ul>
                            </div>
                        </div>
                    </div>
                </header>
                <Switch>
                    <Route exact path='/'><Home data={this.state.configData}/></Route>
                    <Route path='/about'><About /></Route>
                    <Route path='/service'><Service /></Route>
                    <Route path='/contact'><Contact contact={this.state.contactData} /></Route>
                    <Route path='/portfolio'><Portfolio /></Route>
                </Switch>
            </>
        );
    }
}

export default Header;
