import React, {Component} from 'react';
import {Link, Route, Switch} from 'react-router-dom';
// import {AnimatedSwitch} from 'react-router-transition';
// import {TransitionGroup, CSSTransition} from 'react-transition-group';
import Home from './Home';
import About from './About';
import Service from './Service';
import Contact from './Contact';
import Portfolio from './Portfolio';
// import axios from 'axios';

class Header extends Component{
    // constructor(props){
    //     super(props);
    //     this.state = {
    //         configData : {},
    //         contactData : {}
    //     }
    // }
    // componentDidMount(){
    //     axios.get('http://localhost/config')
    //         .then(res => {
    //         this.setState({configData : res.data.result})
    //     });

    //     axios.get('http://localhost/contact')
    //         .then(res => {
    //             this.setState({contactData : res.data.result[0]});
    //             console.table(res.data.result[0]);
    //     })
    // }
    render(){
        // const {configData} = this.state;
        return(
            <>
                <header className="main_menu home_menu">
                    <div className="container">
                        <div className="row align-items-center">
                        <div className="col-lg-12">
                            <nav className="navbar navbar-expand-lg navbar-light">
                            <Link to={'/'}><img src="img/spada/logo.png" alt="logo" style={{width:'50%'}} /></Link>
                            <button
                                className="navbar-toggler"
                                type="button"
                                data-toggle="collapse"
                                data-target="#navbarSupportedContent"
                                aria-controls="navbarSupportedContent"
                                aria-expanded="false"
                                aria-label="Toggle navigation"
                            >
                                <span className="ti-menu"></span>
                            </button>

                            <div
                                className="collapse navbar-collapse main-menu-item justify-content-center"
                                id="navbarSupportedContent"
                            >
                                <ul className="navbar-nav align-items-center">
                                <li className="nav-item">
                                   <Link className={'nav-link'} to={'/'}>Home</Link>
                                </li>
                                <li className="nav-item">
                                    <Link className={'nav-link'} to={'/about'}>About</Link>
                                </li>
                                <li className="nav-item">
                                    <Link className={'nav-link'} to={'/service'}>Services</Link>
                                </li>
                                <li className="nav-item">
                                    <Link className={'nav-link'} to={'/contact'}>Contact</Link>
                                </li>
                                </ul>
                            </div>
                            <div className="social_icon d-none d-lg-block">
                                <a href="# "><i className="ti-facebook"></i></a>
                                <a href="# "><i className="ti-twitter-alt"></i></a>
                                <a href="# "><i className="ti-dribbble"></i></a>
                                <a href="# "><i className="ti-instagram"></i></a>
                            </div>
                            </nav>
                        </div>
                        </div>
                    </div>
                    </header>
                <Switch>
                    <Route exact path='/'><Home/></Route>
                    <Route path='/about'><About /></Route>
                    <Route path='/service'><Service /></Route>
                    <Route path='/contact'><Contact/></Route>
                    <Route path='/portfolio'><Portfolio /></Route>
                </Switch>
            </>
        );
    }
}

export default Header;
